    name = #{name},
    location = #{location},
    template_engine = #{templateEngine}

WHERE id = #{id}

mapper.delete:
DELETE
FROM ASM_CORES

    id = #{id}
    AND location = #{location}

mapper.select:
SELECT *
FROM ASM_CORES

    id = #{id}
    AND location = #{location}
    AND name = #{name}
    AND template_engine = #{templateEngine}

mapper.update:
UPDATE ASMS
name = #{name}, type = #{type}, description = #{description}, asm_core_id = #{core.id,jdbcType=BIGINT}, template = #{template}
WHERE id = #{id}

UPDATE ASMS
SET asm_core_id = #{coreId,jdbcType=BIGINT}
WHERE id = #{id}

mapper.select:
SELECT asm.*, attr.id AS attr_id, attr.asm_id AS attr_asm_id, attr.name AS attr_name, attr.label AS attr_label, attr.type AS attr_type, attr.options AS attr_options, attr.value AS attr_value, attr.large_value AS attr_large_value, attr.ordinal AS attr_ordinal, attr.required AS attr_required, core.id AS core_id, core.location AS core_location, core.name AS core_name, core.template_engine AS core_template_engine FROM ASMS AS asm LEFT JOIN ASM_ATTRS attr ON attr.asm_id = asm.id LEFT JOIN ASM_CORES core ON core.id = asm.asm_core_id
asm.id = #{CQ_PK} asm.id = #{id} asm.name = #{name} AND asm.type = #{type} AND core.location = #{location} AND asm.
ORDER BY ${CQ_ORDERBY}
LIMIT ${CQ_LIMIT}
OFFSET ${CQ_OFFSET}

