mapper.select:
SELECT p.id AS "id", p.name AS "name", p.hname AS "hname", p.description AS "description", p.type AS "type", p.published AS "published", p.template AS "template", p.nav_cached_path AS "nav_cached_path" FROM ASMS AS p WHERE
p.nav_parent_id IS NULL p.nav_parent_id = #{nav_parent_id}
AND p.type LIKE #{page_type} ORDER BY p.hname ASC

